        <meta name="viewport" content="width=device-width, initial-scale=1.0, user-scalable = no" />

        <!--[[ CSS Styles ]]-->
        <link rel="stylesheet" type="text/css" href="/includes2/css/redmond/jquery-ui-1.8.2.custom.css" />
        <link rel="stylesheet" type="text/css" href="/includes2/css/extcalc/main.css" />

        <link rel="stylesheet" type="text/css" href="/includes2/css/extcalc/calculator.css" />
        <link rel="stylesheet" type="text/css" href="/includes2/css/extcalc/redesign.css" />

        <link rel="stylesheet" type="text/css" href="http://fonts.googleapis.com/css?family=Droid+Sans+Mono" />

        <!-- [[ Provides fallback classes for less capable browsers ]]-->
        <script type="text/javascript" src="/includes2/js/modernizr.custom.18239.js"></script>

        <!-- [[ Provides media query capability to old IE ]]-->
        <script type="text/javascript" src="/includes2/js/respond.min.js"></script>
    </head>

    <body id="quote-calculator" class="redesigned">
        <div id="primary-content">
            <!--[[ Header ]]-->
            <div class="header-logo">
                <div class="container">
                    <img src="/includes2/css/extcalc/logo_montway-white.png" alt="Montway Auto Transport" class="logo" />
                    <h3>Car Shipping Calculator</h3>
                </div>
            </div>

            <!--[[ Calculator ]]-->
            <div class="calculator">
                <div class="container">
                    <form id="quoteform" name="quoteform" method="post" action="https://www.montway.com/quote/shipping-rates">
                        <fieldset id="field_locations">

                            <div class="control-group span6 required">
                                <div class="controls">
                                    <input type="text" id="fromtext" name="fromtext" class="calculator-input input-wide city_input" placeholder="From: ZIP -or- City, State">
                                    <select name="fromselect" id="fromselect" style="display:none;">
                                        <option value="">Select One</option>
                                    </select>
                                </div>
                            </div>

                            <div class="control-group span6 required">
                                <div class="controls">
                                    <input type="text" id="totext" name="totext" value="" class="calculator-input input-wide city_input" placeholder="To:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ZIP -or- City, State">
                                    <select name="toselect" id="toselect" style="display:none;">
                                        <option value="">Select One</option>
                                    </select>
                                </div>
                            </div>

                        </fieldset>

                        <fieldset id="field_vehicle-type">

                            <div class="control-group required span6 clearfix">
                            	                                    <select id="automake"  name="automake" onChange="updateModel(document.quoteform.automodel, this.options[this.selectedIndex].value);">
                                        <option value="">Select Make</option>
                                    </select>
                                                            </div>

                            <div class="control-group required span6 clearfix">
                                <div class="controls">
                                                                    <select id="automodel" name="automodel"  >
                                        <option value="">Select Model</option>
                                    </select>
                                                                </div>
                            </div>

                        </fieldset>

                        <fieldset id="field_transport-type" class="form-horizontal">

                            <div class="control-group required">
                                <div class="control-label clearfix">

                                    <span class="label-title">Vehicle Condition</span> <a id="tip-toggle_car-condition" href="javascript:;"><img src="/includes2/css/extcalc/icon_info-blue.png" alt="i" class="icon"></a>

                                    <div id="tip_car-condition" class="tooltip-container">
                                        <div class="container">
                                            <p><strong>Running Vehicle</strong><br>Considered when the car is able to be driven under its own power.</p>
                                            <hr>
                                            <p><strong>Non-running Vehicle</strong><br>Autos with operating engines that are unable to move for whatever reason.</p>
                                        </div>
                                        <div class="caret"></div>
                                    </div>

                                </div>
                                <div class="controls clearfix">
                                    <label class="radio checked">
                                        <input type="radio" name="condition" value="RUNNING" checked="checked"> Running
                                    </label>
                                    <label class="radio">
                                        <input type="radio" name="condition" value="NONRUNNING"> Non-running
                                    </label>
                                </div>
                            </div>

                            <div class="control-group required">
                                <div class="control-label clearfix">

                                    <span class="label-title">Transport Type</span> <a id="tip-toggle_transport-type" href="javascript:;"><img src="/includes2/css/extcalc/icon_info-blue.png" alt="i" class="icon"></a>

                                    <div id="tip_transport-type" class="tooltip-container">
                                        <div class="container">
                                            <p><strong>Open Trailer</strong><br>Cars are exposed to weather elements like rain or dust. The most commonly used delivery method, <em>safe &amp; secure</em>.</p>
                                            <hr>
                                            <p><strong>Enclosed Trailer</strong><br>Cars are not exposed to weather elements. The preferred method for transporting luxury, sports, vintage and custom vehicles, as well classic cars.</p>
                                        </div>
                                        <div class="caret"></div>
                                    </div>
                                </div>
                                <div class="controls clearfix">
                                    <label class="radio checked ">
                                        <input type="radio" name="trailer" value="OPEN" checked="checked"> Open
                                    </label>
                                    <label class="radio">
                                        <input type="radio" name="trailer" value="ENCLOSED"> Enclosed
                                    </label>
                                </div>
                            </div>

                        </fieldset>

                        <input type="submit" id="calculateBtn" value="Calculate Rate" onClick="getDistance(); return false;" class="btn btn-large btn-block btn-submit">

                        <input type="hidden" id="distance" name="distance" value="" />
                        <input type="hidden" id="srcDistnoti" name="srcDistnoti" value="" />
                        <input type="hidden" id="desDistnoti" name="desDistnoti" value="" />
                        <input type="hidden" id="distnoti" name="distnoti" value="" />
                        <input type="hidden" id="fromCity" name="fromCity" value="" />
                        <input type="hidden" id="fromState" name="fromState" value="" />
                        <input type="hidden" id="fromZipGiven" name="fromZipGiven" value="" />
                        <input type="hidden" id="fromZip" name="fromZip" value="" />
                        <input type="hidden" id="fromCounty" name="fromCounty" value="" />
                        <input type="hidden" id="fromLat" name="fromLat" value="" />
                        <input type="hidden" id="fromLong" name="fromLong" value="" />
                        <input type="hidden" id="fromExcludedState" name="fromExcludedState" value="" />
                        <input type="hidden" id="fromStateFullName" name="fromStateFullName" value="" />
