        <!--[[ Calculator functionality ]]-->
        <script type="text/javascript">
            function updateDisplay(which, city, state, zip)
            {
                newAddr = city + ", " + state;
                var thisDisplay = document.getElementById(which + "text");
                if (thisDisplay != null) thisDisplay.value = newAddr;
            }

            var t;
            var focused_class = '';
            var focused_id = '';
            var blankvalue_small = '';

            function getDistance()
            {
                $('#calculateBtn').addClass('loading');

                var fromColor = rgbToHEX($('#fromtext').css('background-color'));
                var toColor = rgbToHEX($('#totext').css('background-color'));

                // Reset the result/status messages.
                displayCalcError("");

                // Put selection options into text input boxes for later processing, if needed
                if (document.getElementById("fromselect").style.display != "none")
                {
                    if ($("#fromselect").val() != 0)
                    {
                        document.getElementById("fromtext").value = document.getElementById("fromselect").value;

                        var fromtextinput = document.getElementById("fromtext");
                        var fromselectinput = document.getElementById("fromselect");

                        fromselectinput.style.display = "none";
                        fromtextinput.style.display = "block";
                    }
                }

                if (document.getElementById("toselect").style.display != "none")
                {
                    if ($("#toselect").val() != 0)
                    {
                        document.getElementById("totext").value = document.getElementById("toselect").value;

                        var totextinput = document.getElementById("totext");
                        var toselectinput = document.getElementById("toselect");

                        toselectinput.style.display = "none";
                        totextinput.style.display = "block";
                    }
                }

                // Check these values to make sure they are populated
                var checkThese = new Array("fromtext"
                                , "totext"
                                , "automake"
                                , "automodel"
                                , "condition"
                                , "trailer"
                                );

                for(var i = 0; i < checkThese.length; i++)
                {
                    var selectElem = document.getElementById(checkThese[i]);

                    if (selectElem != null)
                    {
                        var selectText = selectElem.value;

                        //alert("Checking type[" + selectElem.type + "] value[" + selectText + "]");

                        if(selectText == null || selectText == undefined || selectText == '' || selectText == blankvalue_small)
                        {

                            if (selectElem.type == "text") selectElem.style.backgroundColor = "#FAAFAF";
                            else if (selectElem.type == "select-one")
                            {
                                selectElem.style.color = "#F66";
                                selectElem.style.borderColor = "#F66";
                                selectElem.style.fontWeight = "bold";
                            }
                            selectElem.focus();
                            //displayCalcError("Missing or invalid value.");
                            $('#calculateBtn').removeClass('loading');
                            return false;
                        }
                        else
                        {
                            if (selectElem.type == "text") selectElem.style.backgroundColor = "";
                            else if (selectElem.type == "select-one")
                            {
                                selectElem.style.color = "#3F5DA4";
                                selectElem.style.fontWeight = "normal";
                            }
                        }
                    }
                }

                var addrtext1 = $('#fromtext').val();
                var addrtext2 = $('#totext').val();
                continueSubmit = true;

                $.ajax({
                    type: "GET",
                    url: "http://www.montway.com/quote/geodata?addrtext1=" + addrtext1 + "&addrtext2=" + addrtext2,
                    //data: params,
                    dataType: 'json',
                    success: function (data) {


                        if (fromColor == '#F7C8CA')
                        {
                            if (!isNaN($('#fromtext').val()))
                            {
                                //alert('The zip code entered in the field "Ship car from" is invalid. Please try another zip code OR city name.');
                            }

                            if (isNaN($('#fromtext').val()))
                            {
                                //alert('The city name entered in the field "Ship car from" is invalid. Please check the spelling OR use a zip code.');
                            }
                            $('#fromtext').focus();
                        }
                        else if (toColor == '#F7C8CA')
                        {
                            if (!isNaN($('#totext').val()))
                            {
                                //alert('The zip code entered in the field "Ship car to" is invalid. Please try another zip code OR city name.');
                            }

                            if (isNaN($('#totext').val()))
                            {
                                //alert('The city name entered in the field "Ship car to" is invalid. Please check the spelling OR use a zip code.');
                            }
                            $('#totext').focus();
                        }

                        if (data.distance != null)
                        {
                            checkReturnedData(
                                data,
                                function(code, result)
                                {
                                    if (code != null && result != null)
                                    {
                                        if (code == "from" || code == "to")
                                        {
                                            displayCityStateOptions(code, result, true);
                                        }
                                        else
                                        {
                                            var errorElem;
                                            if (code == "fromerror" || code == "from") errorElem = document.getElementById("fromtext");
                                            if (code == "toerror" || code == "to") errorElem = document.getElementById("totext");

                                            if (errorElem != null) errorElem.style.backgroundColor = "#FAAFAF";

                                            var t = typeof result;

                                            if (t == 'object')
                                            {
                                                result = "Please try again, using the zip code. Otherwise, please check the spelling of city and state and try again.[111]";
                                                displayCalcError(result);
                                            }
                                        }
                                    }
                                    else
                                    {
                                        displayCalcError("Unspecified Error has occurred. Please let us know.");
                                    }
                                }
                            );
                        }
                        else
                        {
                            alert("Could not calculate distance between Pick-up and Drop-off locations. Please check the entered Zip OR City, State and try again. If you have any questions please call Toll free 1-888-666-8929.");
                        }

                        $('#calculateBtn').removeClass('loading');
                    },
                    error: function(jqXHR, textStatus, errorThrown)
                    {
                        alert(textStatus);
                        $('#calculateBtn').removeClass('loading');
                    }
                });
            }

            $(document).ready(function() {
                setupMakeModel();

				$('#automake, #automodel').change(function(){
					$('#automake').parent().find('span.text').html($('#automake').val()?$('#automake').val():'Select Make');
					$('#automodel').parent().find('span.text').html($('#automodel').val()?$('#automodel').val():'Select Model');
				});

                $('.city_input')
                    .keydown(function(e){
                        if ($(this).val() == '')
                        {
                            if(e.keyCode == 8 || e.keyCode == 46 || e.keyCode == 37 || e.keyCode == 39)
                            {
                                return false;
                            }
                            else
                            {
                                $(this).val('');
                                $(this).css({ 'color' : '#3F5DA4' });
                            }
                        }
                    })
                    .keyup(function(e){
                        if ($(this).val().length == 0)
                        {
                            $(this).val('');
                            $(this).css({ 'background' : '#fff' });
                            setGreyAndCaretAtPos($(this).attr('id'), 0, '');
                        }
                        else if (!isNaN($(this).val()))
                        {
                            $(this).attr('maxlength', '5');
                        }
                        else
                        {
                            $(this).attr('maxlength', '255');
                        }

                        if (e.keyCode != 13 && e.keyCode != 40 && e.keyCode != 38 && e.keyCode != 9 && e.keyCode != 16 && e.keyCode != 27 && $(this).val() != '')
                        {
                            autocomplete_cities($(this).attr('id'), 'index.php', 1, e.keyCode, 1);
                        }
                    })
                    .focus(function(){
                        focused_class = $(this).attr('class');
                        focused_id = $(this).attr('id');
                        setGreyAndCaretAtPos($(this).attr('id'), 0, '');
                    })
                    .mouseup(function(e){
                        if (e.which == 3)
                        {
                            focused_id = $(this).attr('id');
                            focused_class = $(this).attr('class');

                            if (focused_class.indexOf('city_input') != -1 && $(this).val() == '')
                            {
                                $(this).css({ 'color' : '#3F5DA4' });
                                $('#' + focused_id).val('');
                            }
                            else if (focused_class.indexOf('city_input') == -1 && $(this).val() != '')
                            {
                                $(this).css({ 'color' : '#3F5DA4' });
                                $('#' + focused_id).val('');
                                focused_class = "";
                                focused_id = "";
                            }
                            else {}
                        }
                    })
                    .blur(function(){
                        $(this).val($(this).val().trim());
                        if ($(this).val() == '')
                        {
                            $(this).css({ 'color' : '#3F5DA4' });
                            $(this).css({ 'background-color' : '#fff' });
                        }
                        else if ($(this).val() == '')
                        {
                            $(this).val('');
                            $(this).css({ 'color' : '#3F5DA4' });
                            $(this).css({ 'background-color' : '#fff' });
                        }
                        else
                        {
                            var el = $(this);
                            $.ajax({
                                url: 'index.php',
                                type: 'POST',
                                data: 'ajax=getCitiesWithStates&query=' + $(this).val() + '&like=0&blur=1',
                                dataType: 'json',
                                async: false,
                                success: function(response)
                                {
                                    if (response.length == 0)
                                    {
                                        el.css({'background-color' : '#F7C8CA'});
                                    }
                                    else
                                    {
                                        el.css({'background-color' : '#fff'});
                                    }
                                }
                            });
                        }
                    })
                    .click(function(){
                        setGreyAndCaretAtPos($(this).attr('id'), 0, '');
                    });

                $('#fromselect')
                    .change(function(){
                        $('#from_select_error').css({ 'display' : 'none' });
                    });

                $('#toselect')
                    .change(function(){
                        $('#to_select_error').css({ 'display' : 'none' });
                    });
            });
        </script>
